def count_hashtags(tList):
    myDict = {}
    # for each string of tweets
    for s in tList:
        words = s.split()
        # for each word in tweet
        for w in words:
            # if that word is a hashtag
            if w[0] == "#":
                # append to our list container of hashtagged words
                myDict[w.lower()[1:]] = 1 + myDict.get(w.lower()[1:], 0)

    sortedDict = dict(sorted(myDict.items(), key=lambda item: (-item[1], item[0])))
    return [("#" + k, v) for k, v in sortedDict.items()]

hashtag_counts = count_hashtags(tweets)
print(hashtag_counts)
